import React, { Fragment , useEffect} from 'react'
import { Button, Container, Form, FormGroup, Input } from 'reactstrap'
import base_url from '../API/BootApi'
import axios from 'axios'

export default function AddUser() {
    const AddUser = () =>{
      useEffect (()=> {
        document.title = "Add User";
      }, [])
    }
    
    //function to add users to server
    const addUsers = () =>{
      axios.post(`${base_url}/addUser`).then(
        (Response) => {
          console.log(response);
        },
        (error)=> {
          console.log(error);
        }
      )

    }

  return (
    <div>
        <Fragment>
          <h2 className='text-center my-3'>Add User Details</h2>
          <Form>
            <FormGroup>
              <lable for="userName"> User Name</lable>
              <Input type="text" placeholder='Enter Name' name="userName" id='userName'>
              </Input >
            </FormGroup>

            <FormGroup>
              <lable for="userAge"> User  Age</lable>
              <Input type="number" placeholder='Enter User Age' name="userAge" id='userAge' min={65}>
              </Input >
            </FormGroup>

            <FormGroup>
              <lable for="userContact"> User Contact Info</lable>
              <Input type="number" placeholder='Enter ContactInfo' name="userContact" id='userContact'>
              </Input >
            </FormGroup>
            <Container>
              <Button color="success ml-5 "> Add Course</Button>
              <Button color="warning ml-4">Clear</Button>
              <Button color="info">Update</Button>
            </Container>
          </Form>
        </Fragment>

    </div>
  )
}
